  font-size: 1rem;
  font-weight: 600;
  border-radius: 4px;
  cursor: pointer;
  transition: background-color 0.2s ease;
}

.retryButton:hover {
  background: #004e92;
}

.retryButton:focus-visible {
  outline: 2px solid #0067b8;
  outline-offset: 2px;
}

client/src/styles/LoadingSpinner.module..content:
.container {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  min-height: 60vh;
  padding: 48px;
}

.spinner {
  width: 64px;
  height: 64px;
  border: 4px solid #e1dfdd;
  border-radius: 50%;
  border-top-color: #0067b8;
  animation: spin 1s linear infinite;
  margin-bottom: 24px;
}

.spinnerInner {
  width: 100%;
  height: 100%;
}

@keyframes spin {
  to {
    transform: rotate(360deg);
  }
}

.loadingText {
  color: #605e5c;
  font-size: 1.125rem;
  text-align: center;
  max-width: 300px;
}

client/src/styles/ProductDetails.module..content:
.details {
  margin-top: 32px;
}

.sectionTitle {
  font-size: 1.5rem;
  font-weight: 600;
  color: #323130;
  margin-bottom: 16px;
  border-bottom: 2px solid #0067b8;
  padding-bottom: 8px;
}

.description {
  margin-bottom: 32px;
}

.featureList {
  display: flex;
  flex-direction: column;
  gap: 12px;
}

.feature {
  display: flex;
  align-items: flex-start;
  gap: 12px;
  color: #323130;
  line-height: 1.6;
}

.feature::before {
  content: '✓';
  color: #107c10;
  font-weight: bold;
  flex-shrink: 0;
  margin-top: 2px;
}

.licenseSection {
  margin-bottom: 32px;
  padding: 24px;
  background: #f8f9fa;
  border-radius: 8px;
  border-left: 4px solid #0067b8;
}

.licenseGrid {
  display: grid;
  gap: 16px;
}

.licenseItem {
  color: #323130;
  line-height: 1.5;
}

.licenseItem strong {
  color: #0067b8;
  display: inline-block;
  min-width: 120px;
}

.systemRequirements {
  padding: 24px;
  background: #ffffff;
  border: 1px solid #e1dfdd;
  border-radius: 8px;
}

.requirementGrid {
  display: grid;
  gap: 16px;
}

.requirement {
  color: #323130;
  line-height: 1.5;
  padding: 12px;
  background: #f8f9fa;
  border-radius: 4px;
}

.requirement strong {
  color: #0067b8;
  display: block;
  margin-bottom: 4px;
}

/* Responsive Design */
@media (max-width: 768px) {
  .details {
    margin-top: 24px;
  }

  .sectionTitle {
    font-size: 1.25rem;
  }

  .licenseSection,
  .systemRequirements {
    padding: 16px;
  }

  .licenseItem strong {
    display: block;
    margin-bottom: 4px;
    min-width: auto;
  }
}

client/src/styles/ProductHeader.module.c.content:
.header {
  margin-bottom: 32px;
}

.breadcrumb {
  margin-bottom: 16px;
}

.breadcrumbList {
  display: flex;
  align-items: center;
  gap: 8px;
  font-size: 0.875rem;
}

.breadcrumbList li:not(:last-child)::after {
  content: '>';
  margin-left: 8px;
  color: #605e5c;
}

.breadcrumbLink {
  color: #0067b8;
  text-decoration: none;
}

.breadcrumbLink:hover {
  text-decoration: underline;
}

.breadcrumbCurrent {
  color: #323130;
  font-weight: 600;
}

.titleSection {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.productType {
  font-size: 0.875rem;
  color: #0067b8;
  font-weight: 600;
  text-transform: uppercase;
  letter-spacing: 0.5px;
}

.title {
  font-size: 2.5rem;
  font-weight: 600;
  color: #323130;
  line-height: 1.2;
  margin: 0;
}

/* Responsive Design */
@media (max-width: 768px) {
  .title {
    font-size: 2rem;
  }

  .breadcrumbList {
    font-size: 0.75rem;
  }
}

client/src/styles/ProductPage.module.css.content:
.container {
  min-height: 100vh;
  background: #f8f9fa;
}

.main {
  flex: 1;
}

.content {
  max-width: 1200px;
  margin: 0 auto;
  padding: 24px;
}

/* New layout matching the reference design */
.productGrid {
  display: grid;
  grid-template-columns: 1fr 400px;
  gap: 48px;
  align-items: start;
  background: #ffffff;
  border-radius: 8px;
  padding: 40px;
  box-shadow: 0 2px 16px rgba(0, 0, 0, 0.05);
}

.leftSection {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

.productHeader {
  display: flex;
  align-items: flex-start;
  gap: 20px;
}

.wordIcon {
  width: 100px;
  height: 100px;
  object-fit: contain;
  flex-shrink: 0;
}

.titleArea {
  flex: 1;
}

.productTitle {
  font-size: 2.5rem;
  font-weight: 600;
  color: #323130;
  margin: 0 0 8px 0;
  line-height: 1.2;
}

.company {
  font-size: 1rem;
  color: #605e5c;
  margin: 0;
  font-weight: 400;
}

.featuresList {
  display: flex;
  flex-direction: column;
  gap: 16px;
}

.features {
  list-style: none;
  padding: 0;
  margin: 0;
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.features li {
  position: relative;
  padding-left: 20px;
  color: #323130;
  font-size: 1rem;
  line-height: 1.5;
}

.features li::before {
  content: '•';
  position: absolute;
  left: 0;
  color: #323130;
  font-weight: bold;
}

.note {
  font-size: 0.875rem;
  color: #605e5c;
  margin: 0;
  font-style: italic;
}

/* Right section - pricing and download */
.rightSection {
  display: flex;
  flex-direction: column;
  gap: 24px;
}

.priceDisplay {
  display: flex;
  flex-direction: column;
  gap: 4px;
}

.freePrice {
  font-size: 2.25rem;
  font-weight: 600;
  color: #0067b8;
  line-height: 1.2;
}

.originalPrice {
  font-size: 1rem;
  color: #605e5c;
  text-decoration: line-through;
}

.savings {
  font-size: 1rem;
  color: #107c10;
  font-weight: 600;
}

.downloadButton {
  width: 100%;
  background: #0067b8;
  color: #ffffff;
  border: none;
  padding: 16px 24px;
  font-size: 1rem;
  font-weight: 600;
  border-radius: 4px;
  cursor: pointer;
  transition: all 0.2s ease-in-out;
  min-height: 48px;
}

.downloadButton:hover:not(:disabled) {
  background: #004e92;
  transform: translateY(-1px);
  box-shadow: 0 4px 12px rgba(0, 103, 184, 0.3);
}

.downloadButton:active:not(:disabled) {
  background: #003d73;
  transform: translateY(0);
}

.downloadButton:disabled {
  opacity: 0.7;
  cursor: not-allowed;
  transform: none;
}

.checkedFeatures {
  display: flex;
  flex-direction: column;
  gap: 8px;
}

.checkItem {
  font-size: 0.875rem;
  color: #323130;
  display: flex;
  align-items: center;
  gap: 8px;
}

.checkItem::before {
  content: '✓';
  color: #107c10;
  font-weight: bold;
  font-size: 1rem;
}

/* Responsive Design */
@media (max-width: 1024px) {
  .productGrid {
    grid-template-columns: 1fr;
    gap: 32px;
  }

  .rightSection {
    order: -1;
  }
}

@media (max-width: 768px) {
  .content {
    padding: 16px;
  }

  .productGrid {
    padding: 24px;
  }

  .productHeader {
    flex-direction: column;
    text-align: center;
    gap: 16px;
  }

  .wordIcon {
    width: 80px;
    height: 80px;
    align-self: center;
  }

  .productTitle {
    font-size: 2rem;
  }

  .freePrice {
    font-size: 1.875rem;
  }
}

client/src/styles/main.css.content:
/* CSS Reset and Global Styles */
*,
*::before,
*::after {
  box-sizing: border-box;
}

* {
  margin: 0;
}

html {
  scroll-behavior: smooth;
}

body {
  line-height: 1.6;
  -webkit-font-smoothing: antialiased;
  font-family: "Segoe UI", -apple-system, BlinkMacSystemFont, "Roboto", "Helvetica Neue", Arial, sans-serif;
  background-color: #f8f9fa;
  color: #323130;
}

img,
picture,
video,
canvas,
svg {
  display: block;
  max-width: 100%;
}

input,
button,
textarea,
select {
  font: inherit;
}

button {
  border: none;
  background: none;
  cursor: pointer;
}

p,
h1,
h2,
h3,
h4,
h5,
h6 {
  overflow-wrap: break-word;
}

ul,
ol {
  list-style: none;
  padding: 0;
}

a {
  color: #0067b8;
  text-decoration: none;
}

a:hover {
  text-decoration: underline;
}

#root {
  isolation: isolate;
  min-height: 100vh;
}

/* Utility Classes */
.visually-hidden {
  position: absolute;
  width: 1px;
  height: 1px;
  padding: 0;
  margin: -1px;
  overflow: hidden;
  clip: rect(0, 0, 0, 0);
  white-space: nowrap;
  border: 0;
}

/* Focus Styles */
*:focus-visible {
  outline: 2px solid #0067b8;
  outline-offset: 2px;
}

/* App Container */
.app {
  min-height: 100vh;
  display: flex;
  flex-direction: column;
}

vite.config.js.content:
import { defineConfig } from 'vite';

export default defineConfig({
  // Set the root to client directory where your frontend code is
  root: 'client',

  // Development server configuration
  server: {
    host: '0.0.0.0',
    port: 3000,
    strictPort: false,
    allowedHosts: 'all',
    // Proxy API calls to your Express server
    proxy: {
      '/api': {
        target: 'http://127.0.0.1:5000',
        changeOrigin: true,
        secure: false,
        configure: (proxy, options) => {
          proxy.on('error', (err, req, res) => {
            console.log('Proxy error:', err.message);
          });
        }
      }
    }
  },

  // Build configuration  
  build: {
    outDir: '../dist/client',
    emptyOutDir: true,
    assetsDir: 'assets',
    rollupOptions: {
      input: 'index.html'
    }
  },

  // Base path for assets
  base: './',

  // Include MSI files as assets
  assetsInclude: ['**/*.msi']
});

