.obf-Link{text-decoration:underline}
.obf-Link:hover{outline:1px dashed transparent;cursor:pointer}
.obf-Link:focus{outline:1px dashed black}
.obf-Link:active{outline:2px solid transparent}
.obf-SubmitButton{height:32px;color:white;width:120px;padding:0;border:0;outline:1px solid transparent;font-family:"wf_SegoeUIRegular","wf_SegoeUI","Segoe UI Regular","Segoe UI","Segoe","Segoe WP","Tahoma","Verdana","Arial","sans-serif";font-size:14px}
.obf-SubmitButton:hover{outline:2px dashed transparent}
.obf-SubmitButton:focus{outline:2px dashed black}
.obf-SubmitButton:active{outline:4px solid transparent}
.obf-SubmitButton[disabled],.obf-SubmitButton[disabled]:hover,.obf-SubmitButton[disabled]:focus,.obf-SubmitButton[disabled]:active{background-color:lightgray}
