.obf-SubmitButton:focus{-ms-high-contrast-adjust:none;outline:2px dashed buttonText;background-color:Highlight;color:HighlightText}
.obf-SubmitButton[disabled],.obf-SubmitButton[disabled]:hover,.obf-SubmitButton[disabled]:focus,.obf-SubmitButton[disabled]:active{-ms-high-contrast-adjust:auto;background-color:unset !important;color:unset !important;outline:2px solid}
}
.obf-CheckBox{outline:1px solid transparent}
.obf-CheckBox:hover{outline:1px dashed transparent}
.obf-CheckBox:focus{outline:1px dashed black}
.obf-CheckBox:active{outline:2px solid transparent}
