.obf-Rating .obf-RatingGraphic{position:absolute;left:0;top:0;height:20px}
.obf-Rtl .obf-Rating .obf-RatingGraphic{left:auto;right:0}
.obf-Rating .obf-RatingGraphic-Filled{opacity:0;fill:#949494;stroke:#949494;z-index:1}
.obf-Rating input:checked + .obf-RatingGraphic-Filled{opacity:.5}
.obf-Rating input:hover + .obf-RatingGraphic-Filled{opacity:1}
.obf-Rating input{opacity:0;display:inline-block;width:20%;height:100%;margin:0;padding:0;z-index:2;position:relative}
.obf-Spinner{position:relative;height:36px;width:36px;margin:auto}
.obf-SpinnerCircle{position:absolute;border-radius:100px;opacity:0}
.obf-Toast{position:var(--obf-position,fixed);bottom:var(--obf-bottom,10px);right:var(--obf-right,10px);z-index:var(--obf-zIndex,10000000000);-webkit-animation-duration:.5s;-moz-animation-duration:.5s;-o-animation-duration:.5s;animation-duration:.5s;-webkit-animation-name:obf-ToastSlideUp;-moz-animation-name:obf-ToastSlideUp;-o-animation-name:obf-ToastSlideUp;animation-name:obf-ToastSlideUp;-webkit-animation-fill-mode:forwards;-moz-animation-fill-mode:forwards;-o-animation-fill-mode:forwards;animation-fill-mode:forwards}
@-webkit-keyframes obf-ToastSlideUp{from{bottom:0;opacity:0}
to{bottom:var(--obf-bottom,10px);opacity:1}
}
@-moz-keyframes obf-ToastSlideUp{from{bottom:0;opacity:0}
to{bottom:var(--obf-bottom,10px);opacity:1}
}
@-o-keyframes obf-ToastSlideUp{from{bottom:0;opacity:0}
to{bottom:var(--obf-bottom,10px);opacity:1}
}
@keyframes obf-ToastSlideUp{from{bottom:0;opacity:0}
to{bottom:var(--obf-bottom,10px);opacity:1}
}
.obf-Toast.obf-Rtl{right:auto;left:10px}
@media(max-height:536px){.obf-ToastZoom{height:calc(79% - var(--obf-bottom, 5px));overflow-y:auto;bottom:var(--obf-bottom,5px);right:var(--obf-right,5px)}
}
#obf-ToastCancel{float:right;cursor:pointer;padding:0 0 15px 15px;border:0;background:0;fill:black}
