#obf-TFormComment{width:100%;height:100px;margin-bottom:6px}
#obf-TFormCommentQuestion{width:100%;margin-bottom:6px;display:block}
#obf-TFormEmailTextBox{width:100%}
.obf-TFormEmailCheckbox{display:inline-block;vertical-align:middle}
#obf-EmailCheckBoxLabel{margin-bottom:8px}
.obf-TFormEmailLabel{display:inline-block;vertical-align:top;width:80%;margin-left:3px;margin-right:3px;padding-bottom:3px}
#obf-TFormSubmitButtonContainer{text-align:right}
#obf-TFormCancelButton{margin-left:10px;margin-right:0}
.obf-Rtl #obf-TFormCancelButton{margin-left:0;margin-right:10px}
.obf-TFormPrivacyConsentText{color:#737373}

